import React, { useState } from 'react';
import Button from '@material-ui/core/Button'; 
import Card from '@material-ui/core/Card';
const App = () => {

    const [count,setcount] = useState(0);
    const incNum = () => {
          setcount(count+1)
    }
     
    const decNum = () =>{
        if(count>0){
            setcount(count-1)
        }
        else {
            alert('wromg input')
        }
        
    }


return (
    <>
   
    <div className="main_div">
    <div className='center_div'>
        <div className="sub_div">{count}</div>
        <Card>
        <Button variant="contained" color="primary" onClick={incNum}>
        Increment   
        </Button>
        
        <span ><Button variant="contained" color="primary" onClick={decNum}>
        Decrement
        </Button></span></Card>
        
    </div>
    </div>
    </>
)
}

export default App;
