import React, { useState } from 'react';

const App = () =>{
 
  const [name,setName] = useState('');
  
  const onSubmit = (event) =>{
  setName(event)     
 }
  


 return (
   <>
        <div>
            <h1>Heloo,{name}</h1><br></br>
            <input type="text" placeholder="enter your name" /><br></br>
            <button onclick={onSubmit} >Click me</button>

        </div>
   </>
 ) 

}

export default App;
