import React, { useState } from "react";


const App = () => {
  const purple =  "violet" ;
  const [bg,setBg] = useState(purple);

  const [textc,setv] = useState('click me');

  const bgChanges = () => {
    let newBg = "blue";
    setBg(newBg);
    setv("oooch");
  }

  const bgback = () =>{
    setBg("black");
    setv("normal");
  }
 return (
   <>
   <div style = { { bg } }>
     <button onClick = {bgChanges}  onDoubleClick = {bgback}>{ textc }</button>
   </div>
   </>
 )
}

export default App;
