const App = () => {
var currTime = new Date().toLocaleTimeString();

const [newTime,setCtime] = useState(currTime);

const UpdateTime = () => {
  let CurrUTime =  new Date().toLocaleTimeString();
  setCtime(CurrUTime);
}
setInterval(UpdateTime,1000);
