import React, { useState } from 'react';

const App = () => {

    const [count,setcount] = useState(0);
    const incNum = () => {
          setcount(count+1)
    }
     
    const decNum = () =>{
        if(count>0){
            setcount(count-1)
        }
        else {
            alert('wromg input')
        }
        
    }


return (
    <>
    <div className="main_div">
    <div className='center_div'>
        <div className="sub_div">{count}</div>
        <button onClick={incNum}>Increment</button>
        <span > <button  onClick={decNum}>Decrement</button> </span>
    </div>
    </div>
    </>
)
}

export default App;
