    const history = useHistory();
    console.log(history);
    return ( 
    <>
    <h1>hello,I am a {fname} {lname} us</h1>
    <p>My current Location is { location.pathname }</p>

    {location.pathname === `/User/vinod/thapa` ? (
    <button onClick={()=>{alert (`you are owsome`)}}>click me</button> ) : null
    }
    </>
    )
}

export default User;
