import ReactDOM from 'react-dom';

let curDate  = new Date();
curDate = curDate.getHours();
curDate = function curtime(curDate){
  if (1 < curDate && curDate < 11) {
    return document.write('good morning');
  }
   else {
    return document.write('night')
  }
}

