import React, { useState } from 'react';

const App = () =>{
 
  const [name,setName] = useState('');
  const [fullName,setFullName] = useState();
  
  const [name1,setName1] = useState('');
  const [fullName1,setFullName1] = useState();

  const inputEvent = (event) =>{
    setName(event.target.value)
               
  }
  const inputEvent1 = (event) =>{
    setName1(event.target.value)
  }

  
  const onSubmit = (event) =>{
    event.preventDefault();
    setFullName(name)
    setFullName1(name)       
 }
  


 return (
   <>   
        <form onSubmit={onSubmit}>
        <div>
            <h1>Heloo,{fullName},{fullName1}</h1><br></br>
            <input type="text" placeholder="enter your name"  onChange={inputEvent}  value={name} /><br></br>
            <input type="text" placeholder="enter your name"  onChange={inputEvent1}  value={name1} /><br></br>
            <button type="submit" >Click me</button>

        </div>
        </form>
   </>
 ) 

}

export default App;
