import React, { useState } from 'react';


const App = () => {
  const [name,setName] = useState();
  const inputEvent = (event) => {
       setName(event)
       
  }
  return (
    <>
    <div>
      <h1>Hello { name }</h1>
      <input type = "text" placeholder="Enter Your Name" onChange={inputEvent} /><br/>
      <button type="submit">Click Me</button>
    </div>
    </>
  )
}

export default App ;
