 }
  


 return (
   <>   
        <form onSubmit={onSubmit}>
        <div>
            <h1>Heloo,</h1><br></br>
            <input type="text" placeholder="enter your First name" name="fname"  onChange={inputEvent}  value={fullName.fname} /><br></br>
