import React, { useState } from "react";


const App = () => {
  const st =  "violet" ;
  const textv = 'click me';

  const [bg,setbg] = useState(st);

  const [textc,setv] = useState(textv);

  const bgChanges = () => {
    setbg("blue" );
    setv("oooch");
  }

  const bgback = () =>{
    setbg("puple");
    setv("normal");
  }
 return (
   <>
   <div style = { bg }>
     <button onClick = {bgChanges}  onDoubleClick = {bgback}>{ textc }</button>
   </div>
   </>
 )
}

export default App;
