import React, { useState } from "react";


const App = () => {
  const purple =  "violet" ;
  const [bg,setBg] = useState(purple);
  const [name,setName] = useState('click me');

  const bgChanges = () => {
    let newBg = "blue";
    setBg(newBg);
    setName("oooch");
  }

  const bgback = () =>{
    setBg(purple);
    setName("normal");
  }
 return (
   <>
   <div style = { { backgroundColor:bg } }>
     <button onMouseEnter = {bgChanges}  onMouseLeave = {bgback}>{ name }</button>
   </div>
   </>
 )
}

export default App;
