import React, { useState } from 'react';

const App = () => {

    const [items,setItems] = useState("buy jjjk");
    
    
    const inputEvent = (event) => {
        setItems(event)
    }

    return  (
    <>
       <div className="main_div">
           <div className="center_div">
              <br />
              <h1>toDo List</h1>
              <br />
              <input type="text" placeholder="Add a Items" onChange={inputEvent} value={items}/>
              <button > + </button>
              <ol>
                  <li>  {items} </li>
              </ol>
           </div>
       </div>
    </>
    )
}

export default App;
